$(function(){var t=document.querySelector(".js-emailcopybtn");t.addEventListener("click",function(e){var o=document.querySelector(".js-emaillink"),c=document.createRange();c.selectNode(o),window.getSelection().addRange(c);try{var n=document.execCommand("copy")?"successful":"unsuccessful";console.log("Copy email command was "+n)}catch(e){console.log("Oops, unable to copy")}window.getSelection().removeAllRanges(),t.innerHTML="Copied",t.classList.add("promo__copy--complete"),t.addEventListener("mouseleave",function(){t.innerHTML="Copy<span> code</span>",t.classList.remove("promo__copy--complete")})})});